INSERT INTO `music_sargams` (`id`,`song_names`,`sargam`,`url`) VALUES (412,'Tum mile to jeena aa gaya - Notes & Sargam','Sargam (notations) for Tum mile to jeena aa gaya\n \nFilm: Tum Mile (2009)\n Singer : Neeraj Sridhar\nPrelude:\n Re Ga Pa# Pa# Dha# Sa Re\n Re Ga Pa# Pa# Dha# Sa Re\n Pa Ma Ma Ga Ga Re Sa Ni# Sa Re Ga Re\n Re Ga Pa# Pa# Dha# Sa Re\n Re Ga Pa# Pa# Dha# Sa Re\n Pa Ma Ma Ga Sa Re Re Sa\nTu hi meri, hai saari zameen\n ga pa pa pa, pa pa pa dha pa\nChahe kahin se chalun\n pa ma ma ga sa re ga re\nTujh pe hi aake ruku\n pa ma ma ga sa re sa sa\nTere siva, main jaun kahan\n ga pa pa pa, pa pa pa dha pa\nKoi bhi raah chunu\n pa ma ma ga sa re ga re\nTujh pe hi aake ruku\n pa ma ma ga sa re sa sa\nTum Mile, To Lamhein Tham gaye\n pa# pa ga, ga ga re sa sa re pa re\nTum mile, to Saare gum gaye\n re ga re sa, sa sa ni# dha# dha# ni# ga ni#\nTum mile, to muskhurana, aa gaya\n dha# pa# dha#, dha# dha# ni# sa pa#, re ni# sa ga\nTum mile to jadoo chaa gaya\n pa# pa ga, ga ga re sa sa re pa re\nTum mile to jeena aa gaya\n re ga re sa, sa sa ni# dha# dha# ni# ga ni#\nTum mile to mene paya hai khuda\n dha# pa# dha#, dha# dha# ni# sa pa#, re ni# sa\n','Notes---Sargam-Tum-mile-to-jeena-aa-gaya');
INSERT INTO `music_sargams` (`id`,`song_names`,`sargam`,`url`) VALUES (413,'Tum Dil ki dhadkan me - Notes & Sargam','Film: Dhadkan (2000)\n Singer : Abhijit Bhattacharya\nTum Dil ki dhadkan me\n ma dha saÔÇÖ ni ni dha pa dha\nRahte ho rahte ho\n ga ga pa ni dha dha pa ma\nBaahon mein aajao, sapnon mein khojaao\n maÔÇÖ maÔÇÖ saÔÇÖ reÔÇÖ reÔÇÖ ni saÔÇÖsaÔÇÖ ni dha ni dha ma\nDeewano sa haal hua, humko unse pyaar hua\n saÔÇÖsaÔÇÖ saÔÇÖ saÔÇÖ saÔÇÖ saÔÇÖ gaÔÇÖ ni ni ni ni re ni dha pa dha dha dha\nDeewano sa haal hua, humko unse pyaar hua\n saÔÇÖsaÔÇÖ saÔÇÖ saÔÇÖ saÔÇÖ saÔÇÖ gaÔÇÖ ni ni ni ni re ni dha pa dha dha dha\nDheere se voh paas aaye, chupke se izhaar hua\n saÔÇÖsaÔÇÖ saÔÇÖ saÔÇÖ saÔÇÖ saÔÇÖ saÔÇÖ ni ni ni ni re ni dha pa dha dha dha\nAb naa kisise darna hai , sang jeena marna hai\n ma dha dha saÔÇÖsaÔÇÖ ni ni dha pa dha , ga pa pa ni ni dha pa pa ma\nBaahon mein aajao, sapnon mein khojaao\n maÔÇÖ maÔÇÖ saÔÇÖ reÔÇÖ reÔÇÖ ni saÔÇÖsaÔÇÖ ni dha ni dha ma\nTum Dil ki dhadkan me\n ma dha saÔÇÖ ni ni dha pa dha\nRahte ho rahte ho\n ga ga pa ni dha dha pa ma\n','Notes---Sargam-Tum-Dil-ki-dhadkan-me');
